import styled from 'styled-components';
import { createGlobalStyle } from 'styled-components';
// import PlusJakartaSansMedium from '../assets/fonts/static/PlusJakartaSans-Medium.ttf'

export const Container = styled.div`

    @font-face {
    font-family: 'PlusJakartaSansMedium';
    src: url('../assets/fonts/static/PlusJakartaSans-ExtraBold.ttf') format('ttf');
    font-style: normal;
    font-weight: 700;
    font-display: fallback;
    }

    * {
        font-family: 'PlusJakartaSansMedium', sans-serif;
    }

    background: hsl(210, 60%, 98%);
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;

    #box {
        background: hsl(0, 0%, 100%);
        width: 45vw;
        height: 90%;
        border-radius: 15px;
        padding: 25px 20px;
        border: 1px solid black;
    }

    #top-note {
        display: flex;
        align-items: center;
        justify-content: space-between;
        box-sizing: content-box;
        margin: 0 0 20px 0;

        h1 {
            color: black;
            font-size: 25px;

            span {
                padding: 3px 9px;
                background: hsl(219, 85%, 26%);
                font-size: 18px;
                color: hsl(0, 0%, 100%);
                border-radius: 6px;
            }
        }

        .mark-read {
            cursor: pointer;
            color: hsl(219, 14%, 63%);

            &:hover {
                color: hsl(219, 85%, 26%);
            }
        }
    }

    .singular-noti {
        margin: 10px 0;
    }

    .normal {
        place-content: center;
        display: grid;
        grid-template-columns: 75px 1fr;
        grid-template-rows: 1fr 1fr;
        grid-template-areas: 'imageProfile textNoti'
                             'imageProfile timeNoti';
        border-radius: 7px;
        background: red;
    }

    .profile-image {
        width: 50px;
        height: 50px;
        grid-area: imageProfile;
    }

    .text-noti {
        grid-area: textNoti;
    }

    .time {
        grid-area: timeNoti;
    }
`;
