import styled from 'styled-components';
import { createGlobalStyle } from 'styled-components';
// import PlusJakartaSansMedium from '../assets/fonts/static/PlusJakartaSans-Medium.ttf'

export const Container = styled.div`

@font-face {
  font-family: 'PlusJakartaSansMedium';
  src: url('../assets/fonts/static/PlusJakartaSans-ExtraBold.ttf') format('ttf');
  font-style: normal;
  font-weight: 700;
  font-display: fallback;
}

    background: hsl(210, 60%, 98%);
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;

    #box {
        background: hsl(0, 0%, 100%);
        width: 45vw;
        height: 90%;
        border-radius: 15px;
        padding: 20px 15px;
        border: 1px solid black;
    }

    #top-note {
        display: flex;
        align-items: center;
        justify-content: space-between;

        h1 {
            color: black;
            font-family: 'PlusJakartaSansMedium', sans-serif;
            font-size: 25px;

            span {
                padding: 3px 9px;
                background: hsl(219, 85%, 26%);
                font-size: 18px;
                color: hsl(0, 0%, 100%);
                border-radius: 6px;
            }
        }

        span {
            cursor: pointer;
            color: hsl(219, 14%, 63%);

            &:hover {
                color: hsl(219, 12%, 42%);
            }
        }
    }

    .singular-noti {
        margin: 4px 0;
    }

    .profile-image {
        width: 50px;
        height: 50px;
    }
`;
