import styled from 'styled-components';
import { createGlobalStyle } from 'styled-components';
import PlusJakartaSansMedium from '../assets/fonts/static/PlusJakartaSans-Medium.ttf'

export default createGlobalStyle`
    @font-face {
        font-family: 'PlusJakartaSans Medium';
        src: local('PlusJakartaSans'),
        url(${PlusJakartaSansMedium}) format('ttf');
        font-weight: 500;
        font-style: normal;
    }
`;

export const Container = styled.div`

    * {
        border: 1px solid red;
    }
    background: hsl(210, 60%, 98%);
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;

    #box {
        background: hsl(0, 0%, 100%);
        width: 45vw;
        height: 90%;
        border-radius: 15px;
        padding: 20px 15px;
        border: 1px solid black;
    }

    #top-note {
        display: flex;
        align-items: center;

        h1 {
            color: black;
            font-family: 'PlusJakartaSansMedium', 'sans-serif';

            span {
                width: 20px;
                height: 20px;
                background: hsl(219, 85%, 26%);
                font-size: 22px;
            }
        }
    }

    .singular-noti {
        margin: 4px 0;
    }

    .profile-image {
        width: 50px;
        height: 50px;
    }
`;
